<!-- CMS Infotmation --><div class="grid_6">    <div class="block-border">        <div class="block-header">            <h1>Cms Information</h1><span></span>        </div>        <div class="block-content">            <ul class="block-list">                <li>CMS Version: <strong>0.0.1a</strong></li>                <li>Database Version: <strong>0.0.1a</strong></li>            </ul>        </div>        <div class="block-content dark-bg">            <p><div id="update">Checking for updates &nbsp;<img src="{TEMPLATE_URL}/img/icons/small/loading.gif"></div></p>        </div>    </div></div><!-- Server Information --><div class="grid_6">    <div class="block-border">        <div class="block-header">            <h1>Server Information</h1><span></span>        </div>        <div class="block-content">            <ul class="block-list">                <li>PHP Version: <strong>{php_version}</strong></li>                <li>{driver} Version: <strong>{database_version}</strong></li>            </ul>        </div>        <div class="block-content dark-bg">            <p><a href="{SITE_URL}/admin/phpinfo">View PHP Info</a></p>        </div>    </div></div><!-- Control Panel --><div class="grid_12">    <div class="block-border">        <div class="block-header">            <h1>Control Panel</h1><span></span>        </div>        <div class="block-content">            <ul class="shortcut-list">                <li>                    <a href="{SITE_URL}/admin/users">                        <img src="{TEMPLATE_URL}/img/icons/users-icon.png">                        Manage User Accounts                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/groups">                        <img src="{TEMPLATE_URL}/img/icons/user-groups.png">                        User Groups & Permissions                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/settings">                        <img src="{TEMPLATE_URL}/img/icons/config-icon.png">                        Edit Site Configuration                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/registration">                        <img src="{TEMPLATE_URL}/img/icons/reg-icon.png">                        Registration Keys & Settings                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/news">                        <img src="{TEMPLATE_URL}/img/icons/news-icon.png">                        Manage News Posts                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/realms">                        <img src="{TEMPLATE_URL}/img/icons/realm-icon.png">                        Realm Managment                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/characters">                        <img src="{TEMPLATE_URL}/img/icons/char-icon.png">                        Character <br />Editor                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/modules">                        <img src="{TEMPLATE_URL}/img/icons/module-icon.png">                        Module Managment                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/vote">                        <img src="{TEMPLATE_URL}/img/icons/vote-icon.png">                        Vote System Settings                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/shop">                        <img src="{TEMPLATE_URL}/img/icons/shop-icon.png">                        Shop System Packages                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/donate">                        <img src="{TEMPLATE_URL}/img/icons/donate-icon.png">                        Donation Packages                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/statistics">                        <img src="{TEMPLATE_URL}/img/icons/stats-icon.png">                        Website Statistics                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/support">                        <img src="{TEMPLATE_URL}/img/icons/faq-icon.png">                        Manage Site Support / Faq's                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/templates">                        <img src="{TEMPLATE_URL}/img/icons/theme-icon.png">                        Manage Templates                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/console">                        <img src="{TEMPLATE_URL}/img/icons/console-icon.png">                        Send Console Command                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/update">                        <img src="{TEMPLATE_URL}/img/icons/update-icon.png">                        CMS Remote Updater                    </a>                </li>                <li>                    <a href="{SITE_URL}/admin/logs">                        <img src="{TEMPLATE_URL}/img/icons/logs-icon.png">                        View Logs                    </a>                </li>            </ul>            <div class="clear"></div>        </div>    </div></div>